mapper.select:
select user_email userEmail,
        user_id userId,
        user_name userName
from tbl_user
  where user_email = #{userEmail}
 and user_password = #{userPassword}

mapper.update:
update tbl_user SET
user_name =#{userName},
user_password = #{userPassword}
where user_id = #{userId}

update tbl_user SET
user_state = '1'
where user_id = #{userId}
